<!doctype html><html lang="en"><head>    <meta charset="UTF-8">    <meta name="viewport" content="width=device-width, user-scalable=no, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0">    <meta http-equiv="X-UA-Compatible" content="ie=edge">    <title>Redirecting Marker</title>    <link rel="stylesheet" href="https://unpkg.com/leaflet@1.9.4/dist/leaflet.css"          integrity="sha256-p4NxAoJBhIIN+hmNHrzRCf9tD/miZyoHS5obTRR9BMY="          crossorigin=""/>    <script src="https://unpkg.com/leaflet@1.9.4/dist/leaflet.js"            integrity="sha256-20nQCchB9co0qIjJZRGuk2/Z9VM+kNiyxNV1lvTlZBo="            crossorigin=""></script>    <style>        #map { height: 500px; width: 100%; }    </style></head><body><h1>Clickable Marker Example</h1><div id="map"></div><script>    // Initialize the map and set view to Manila    var map = L.map('map').setView([14.5995, 120.9842], 13);    // Load OpenStreetMap tiles    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {        maxZoom: 19,        attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'    }).addTo(map);    // Add a marker at Manila's coordinates    var marker = L.marker([14.5995, 120.9842]).addTo(map);    // Add click event to the marker    marker.on('click', function() {        window.location.href = "location.php"; // Redirect to desired page    });    // Optional: Add a popup to the marker    marker.bindPopup("Click me to go to the page!").openPopup();</script></body></html>